import React,{useContext} from "react";
import { Link } from "react-router-dom";
//Function
import { shorten, isInCart,quantityCount } from "../../helpers/functions";
//Context
import { cartContext } from "../../context/CartContextProvider";
//Icon
import trashIcon from "../../assets/icons/trash.svg";
//Style
import styles from "./ Product.module.css";

const Product=({productData})=>{
    const {state,dispath}= useContext(cartContext);
    return(
        <div className={styles.container}>
            <img cl4 src={productData.image} alt='products' style={{width:"200px"}}/>
            <h3>{shorten(productData.title) }</h3>
            <p>{productData.price}</p>
            <div>
                <Link to={`/products/${productData.id}`}>Ditails</Link>
                <div>
                     {quantityCount(state,productData.id)>1 && <button onClick={()=>dispath({type:"DECREASE",payload:productData})}>-</button>}
                        {quantityCount(state,productData.id)===1 && <button onClick={()=>dispath({type:"REMOVE_ITEM",payload:productData})}><img src={trashIcon} alt="trash" style={{width:"20px"}}/></button>}
                    {
                        isInCart(state,productData.id)?
                        <button onClick={()=>dispath({type:"INCREASE", payload:productData })}>+</button>:
                        <button onClick={()=>dispath({type:"ADD_ITEM",payload:productData})}>Add to cart</button>
                    }
                    
                       
                    
                </div>
            </div>
        </div>
    )
}
export default Product;
