import React,{useReducer,createContext} from "react";

const initialState={
    selectedItems :[],
    itemCounters:0,
    total:0,
    checkout:false
}

const sumItems=(items)=>{
    const itemCounter=items.reduce((total,product)=>total+product.quantity,0);
    const total=items.reduce((total,product)=>total+product.price*product.quantity).toFixed(2);
    return {itemCounter,total}
}
const cartReducer=(state,action)=>{
    console.log(state)
    switch(action.type){
        case "ADD_ITEM":
            if(!state.selectedItems.find(item=>item.id===action.payload.id)){
                state.selectedItems.push({
                    ...action.payload,quantity:1
                })
            }
            return{...state,
                   selectedItems:[...state.selectedItems],
                   ...sumItems(state.selectedItems)
                }
        
        case "REMOVE_ITEM":
            const newSelectedItems=state.selectedItems.filter(item=>item.id !==action.payload.id);
            return {...state,selectedItems:[...newSelectedItems],
                   ...sumItems(state.selectedItems)}
        case "INCREASE":
            const indexI=state.selectedItems.findIndex(item=>item.id===action.payload.id);
            state.selectedItems[indexI].quantity++;
            return{...state,
                   ...sumItems(state.selectedItems)}
        case "DECREASE":
            const indexD=state.selectedItems.findIndex(item=>item.id===action.paload.id);
            state.selectedItems[indexD].quantity--;
            return{...state,
                   ...sumItems(state.selectedItems)}
        case "CHECKOUT":
            return{
                selectedItems :[],
                itemCounters:0,
                total:0,
                checkout:true
            }
        case "CLEAR":
            return{
                selectedItems :[],
                itemCounters:0,
                total:0,
                checkout:false
            }
        default: 
            return state;

    }
}
export const cartContext=createContext();
const CartContextProvider=({children})=>{
    const[state,dispath]=useReducer(cartReducer,initialState)
    return(
        <cartContext.Provider value={{state,dispath}}>
          {children}
        </cartContext.Provider>
    )
}
export default CartContextProvider;
